import React from 'react';
import { useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import '../Login/Login.scss';
import { loginApiCall} from '/'

function Login() {
    const navigate = useNavigate();

    let email;
    let password;

    // const handleLogin = () => {
    //     console.log("Login button clicked");
    //     // loginApiCall()
    // }

    const handleLogin = () => {
        console.log("Login button clicked");
        const payload = { email, password };

        loginApiCall(payload)
            .then(data => {
                console.log("Login successful:", data);
                // Navigate to the desired page after successful login
                navigate('/dashboard');
            })
            .catch(error => {
                console.error("Login error:", error);
                // Handle login error (e.g., show an error message)
            });
    }

  return (
    <>
     <div className="Login-container">
            <h1 className="Login-title">Fundo</h1>
            <h2 className="Login-subtitle">Sign in</h2>
            <p className="Login-description">Use your Fundoo Account</p>
            <form className="Login-form">
                <TextField id="outlined-basic" onChange={(e)=>email=e.target.value} label="Email or phone*" variant="outlined"/>
                <br/>
                 <TextField id="outlined-basic" onChange={(e) => password = e.target.value} label="Password*" variant="outlined"/>
                 <br/>
                 <div className="Login-links">
                {/* eslint-disable-next-line jsx-a11y/anchor-is-valid */}

                    <a href="#" className="link">Forgot password</a>
                    <span className="link"> | </span>

                {/* eslint-disable-next-line jsx-a11y/anchor-is-valid */}

                <a href="#" className="link" onClick={() => navigate('/signup')}>Create account</a>
                </div>
                <br/>
                {/* <button type="submit" className="button">Login</button> */}
                <Button variant="contained" onClick={handleLogin} >Login</Button>
            </form>
            <div className="Login-footer">
                <select className="select">
                    <option>English (United States)</option>
                </select>
                <span className="link">Help</span>
                <span className="link">Privacy</span>
                <span className="link">Terms</span>
            </div>
        </div>
    </>
  );
}

export default Login;
