import React, { useState } from 'react';
import RoutingModule from './RoutingModule';
import './App.css';
import NoteBar from './components/NoteBar/NoteBar';

function App() {
  const [isNoteBarOpen, setIsNoteBarOpen] = useState(false);

  const toggleNoteBar = () => {
    setIsNoteBarOpen(!isNoteBarOpen);
  };

  return (
    <>
      <button onClick={toggleNoteBar}>Open Note Bar</button>
      {isNoteBarOpen && <NoteBar onClose={toggleNoteBar} />}
      <RoutingModule />
    </>
  );
}

export default App;
